(function calculatedFieldValue(current) {
var count = new GlideAggregate('x_dptmgr_deployment_plan');
count.addQuery('associated_deployment.sys_id', current.sys_id);
count.addEncodedQuery('deployment_type=Core Deployment^task_status=Complete');
count.addAggregate('COUNT');
count.query();
var totalsteps = 0;
if(count.next()) 
   totalsteps = count.getAggregate('COUNT');

	return totalsteps;  // return the calculated value

})(current);

