sys_widgets.script:
function sections() {
    return {
        'widget_name': { 'uiPageName' : 'x_dptmgr_DM_Estimated_time_of_completion'},
        'widget_name2': {'uiPageName': 'x_dptmgr_DM_Estimated_time_of_completion'}
    };
}

function render() {
    var uiPageName = renderer.getPreference('uiPageName');
    return renderer.getRenderedPage(uiPageName);
}

function getEditLink() {
    return "sys_ui_page.do?sysparm_query=name=" + renderer.getPreferences().get("uiPageName");
}
